create or replace package hma_string is
   function split (
      p_str       in varchar2,
      p_delimiter in varchar2
   ) return hma_varchar2.hma_t_varchar2;

   function join (
      p_list      in hma_varchar2.hma_t_varchar2,
      p_delimiter in varchar2
   ) return varchar2;
end hma_string;
