create table hma.strava_token (
    token_id      number
        generated by default as identity minvalue 1 maxvalue 9999999999999999999999999999 increment by 1 start with 1 cache 20 noorder
        nocycle nokeep noscale
    not null enable,
    access_token  varchar2(255 byte),
    refresh_token varchar2(255 byte),
    expires_at    number,
    last_updated  date default sysdate
);

alter table hma.strava_token
    add constraint pk_strava_token primary key ( token_id )
        using index enable;


